import React, { useState, useEffect } from "react";
import { ChevronDown } from "lucide-react";
import "./CreateContract.css";
import Navbar from "../Navbar/Navbar";
import Cookies from 'js-cookie'; 

export default function CreateContract() {
  const [formData, setFormData] = useState({
    ownerName: "",
    ownerEmail: "",
    tenantName: "",
    tenantEmail: "",
    street: "",
    exteriorNumber: "",
    neighborhood: "",
    postalCode: "",
    state: "",
    country: "",
    startDate: "",
    endDate: "",
    rentAmount: "",
    securityDeposit: "",
    clauses: {},
  });

  const [clauses, setClauses] = useState([]);

  useEffect(() => {
    const fetchClauses = async () => {
      try {
        const response = await fetch(
          "https://ezcontract-e556acf4694e.herokuapp.com/api/public/clauses",
          {
            method: "GET",
            credentials: "include", 
          }
        );
        if (response.ok) {
          const data = await response.json();
          setClauses(data.content);
        } else {
          console.error("Error al obtener las clÃ¡usulas.");
        }
      } catch (error) {
        console.error("Error en la solicitud:", error);
      }
    };
    fetchClauses();
  }, []);

  const handleInputChange = (e) => {
    const { name, checked, type, value } = e.target;
    if (type === "checkbox" && name.startsWith("clause-")) {
      setFormData((prevState) => ({
        ...prevState,
        clauses: {
          ...prevState.clauses,
          [name]: checked,
        },
      }));
    } else {
      setFormData((prevState) => ({
        ...prevState,
        [name]: type === "checkbox" ? checked : value,
      }));
    }
  };

  const handleSaveContract = async () => {
    const selectedClauses = Object.keys(formData.clauses)
      .filter((clauseKey) => formData.clauses[clauseKey])
      .map((clauseKey) => ({ id: parseInt(clauseKey.split("-")[1], 10) }));

    const payload = {
      ownerName: formData.ownerName,
      ownerEmail: formData.ownerEmail,
      renterName: formData.tenantName,
      renterEmail: formData.tenantEmail,
      street: formData.street,
      numberHouse: formData.exteriorNumber,
      suburb: formData.neighborhood,
      zip: formData.postalCode,
      state: formData.state,
      country: formData.country,
      startContract: formData.startDate,
      endContract: formData.endDate,
      cost: parseFloat(formData.rentAmount),
      clausesId: selectedClauses,
    };

    try {
      const token = Cookies.get('quackCookie');
      const response = await fetch(
        "https://ezcontract-e556acf4694e.herokuapp.com/api/users/contract",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
          body: JSON.stringify(payload),
          credentials: "include",
        }
      );
      if (response.ok) {
        console.log("Contrato guardado exitosamente.");
      } else {
        console.error("Error al guardar el contrato.");
      }
    } catch (error) {
      console.error("Error en la solicitud:", error);
    }
  };

  return (
    <>
      <Navbar />
      <div className="contract-form">
        <h1>Crear un nuevo contrato</h1>
        <p className="subtitle">Rellene el siguiente formulario</p>

        <form>
          <section>
            <h2>Datos del propietario</h2>
            <div className="form-row">
              <div className="form-group">
                <label>Nombre completo del propietario</label>
                <input
                  type="text"
                  name="ownerName"
                  placeholder="Ingrese el nombre completo segÃºn una identificaciÃ³n oficial"
                  value={formData.ownerName}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Correo electrÃ³nico del propietario</label>
                <input
                  type="email"
                  name="ownerEmail"
                  placeholder="ejemplo@email.com"
                  value={formData.ownerEmail}
                  onChange={handleInputChange}
                />
              </div>
            </div>
          </section>

          <section>
            <h2>Datos del inquilino</h2>
            <div className="form-row">
              <div className="form-group">
                <label>Nombre completo del inquilino</label>
                <input
                  type="text"
                  name="tenantName"
                  placeholder="Ingrese el nombre completo segÃºn una identificaciÃ³n oficial"
                  value={formData.tenantName}
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Correo electrÃ³nico del inquilino</label>
